'use strict';

const promisify = require('promisify-es6');

const findSources = require('../utils/find-sources');

module.exports = send => {
  return promisify(function () {
    const _findSources = findSources(Array.prototype.slice.call(arguments)),
          callback = _findSources.callback,
          sources = _findSources.sources,
          opts = _findSources.opts;

    send({
      path: 'files/cp',
      args: sources,
      qs: opts
    }, error => callback(error));
  });
};
